import React, { useContext } from "react";
import { useState } from "react";

import "./ProductFilter.css";

const ProductFilter = (props) => {
  const dropdownChangeHandler = (event) => {
    props.filterFunction(event.target.value)
  };
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by Vehicle</label>
        {/* <select onChange={dropdownChangeHandler}>
          <option value="SUV">SUV</option>
          <option value="SEDAN">SEDAN</option>
          <option value="ECO">ECO</option>
          <option value="ACCESSIBLE">ACCESSIBLE</option>
        </select> */}
        <label>SUV</label>
        <input type='checkbox' value='SUV' name='filterValue' onChange={dropdownChangeHandler}/>
        <label>SEDAN</label>
        <input type='checkbox' value='SEDAN' onChange={dropdownChangeHandler}/>
      </div>
    </div>
  );
};

export default ProductFilter;
